	DBMS_LOB.APPEND(clob_loc6, clob6_pt2);
	DBMS_LOB.APPEND(clob_loc6, clob6_pt3);
	DBMS_LOB.APPEND(clob_loc6, clob6_pt4);
	DBMS_LOB.APPEND(clob_loc6, clob6_pt5);
	DBMS_LOB.APPEND(clob_loc6, clob6_pt6);

	DBMS_LOB.APPEND(clob_loc7, clob7_pt1);
	DBMS_LOB.APPEND(clob_loc7, clob7_pt2);


	UPDATE StudentAnswers
	SET
		Answer1 = clob_loc1,
		Answer2 = clob_loc2,
		Answer3 = clob_loc3,
		Answer4 = clob_loc4,
		Answer5 = clob_loc5,
		Answer6 = clob_loc6,
		Answer7 = clob_loc7
	WHERE
